mapper.insert:
INSERT INTO member (
					member_email
					, member_pwd
					, member_nm
					, member_zip
					, member_baseaddr
					, member_detaaddr
					
					, member_bizno
					
					, member_cntinfo
					, member_grd
					)
VALUES (
					#{memberEmail}
					, #{memberPwd}
					, #{memberNm}
					, #{memberZip}
					, #{memberBaseaddr}
					, #{memberDetaaddr}
					
					, #{memberBizno}
					
					, #{memberCntinfo}
					, #{memberGrd}
					)

mapper.select:
SELECT COUNT(*)
FROM member
WHERE member_email = #{memberEmail}
